contracts/SolanaBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./WrappedSOL.sol";
import "./ValidatorRegistry.sol";

/**
 * @title SolanaBridge
 * @notice Main bridge contract for minting and burning wrapped SOL tokens
 * @dev Implements multi-signature validation, fee mechanism, and security features
 */
contract SolanaBridge is ReentrancyGuard, Pausable, Ownable {
    WrappedSOL public wrappedSOL;
    ValidatorRegistry public validatorRegistry;

    mapping(uint256 => bool) public processedNonces;
    uint256 public feeBasisPoints; // Fee in basis points (50 = 0.5%, 100 = 1%)

    event TokensMinted(
        address indexed user,
        uint256 amount,
        uint256 nonce,
        uint256 timestamp
    );

    event TokensBurned(
        address indexed user,
        uint256 amount,
        uint256 nonce,
        uint8 destChain, // 0=Solana, 2=Sui
        bytes destAddress,
        uint256 timestamp
    );

    event FeeUpdated(uint256 oldFee, uint256 newFee);
    event ValidatorRegistryUpdated(address indexed oldRegistry, address indexed newRegistry);

    /**
     * @notice Constructor initializes the bridge with required contracts
     * @param _wrappedSOL Address of the WrappedSOL token contract
     * @param _validatorRegistry Address of the ValidatorRegistry contract
     * @param _feeBasisPoints Initial fee in basis points (max 10000 = 100%)
     */
    constructor(
        address _wrappedSOL,
        address _validatorRegistry,
        uint256 _feeBasisPoints
    ) Ownable(msg.sender) {
        require(_wrappedSOL != address(0), "Invalid wSOL address");
        require(_validatorRegistry != address(0), "Invalid registry address");
        require(_feeBasisPoints <= 10000, "Fee too high");

        wrappedSOL = WrappedSOL(_wrappedSOL);
        validatorRegistry = ValidatorRegistry(_validatorRegistry);
        feeBasisPoints = _feeBasisPoints;
    }

    /**
     * @notice Mint wrapped SOL tokens (called by relayer with validator signatures)
     * @param user Recipient address
     * @param amount Amount to mint (in wei, 18 decimals)
     * @param nonce Unique nonce from Solana bridge
     * @param signatures Array of validator signatures
     * @dev Requires valid multi-sig, prevents replay attacks via nonce
     */
    function mintWrapped(
        address user,
        uint256 amount,
        uint256 nonce,
        bytes[] calldata signatures
    ) external nonReentrant whenNotPaused {
        require(user != address(0), "Invalid user address");
        require(amount > 0, "Amount must be > 0");
        require(!processedNonces[nonce], "Nonce already used");

        // Construct message to verify
        bytes32 message = keccak256(abi.encodePacked(
            user,
            amount,
            nonce,
            block.chainid,
            address(this)
        ));

        // Verify validator signatures
        require(
            validatorRegistry.verifySignatures(message, signatures),
            "Invalid signatures"
        );

        // Mark nonce as processed (prevents replay attacks)
        processedNonces[nonce] = true;

        // Calculate fee
        uint256 fee = (amount * feeBasisPoints) / 10000;
        uint256 amountAfterFee = amount - fee;

        // Mint tokens to user
        wrappedSOL.mint(user, amountAfterFee);

        // Mint fee to owner
        if (fee > 0) {
            wrappedSOL.mint(owner(), fee);
        }

        emit TokensMinted(user, amountAfterFee, nonce, block.timestamp);
    }

    /**
     * @notice Burn wrapped SOL to bridge back to Solana/Sui
     * @param amount Amount to burn
     * @param destChain Destination chain (0=Solana, 2=Sui)
     * @param destAddress Destination address (32 bytes for Solana/Sui, 20 for Ethereum)
     * @dev Burns tokens and emits event for relayer to process
     */
    function burnAndBridge(
        uint256 amount,
        uint8 destChain,
        bytes calldata destAddress
    ) external nonReentrant whenNotPaused {
        require(amount > 0, "Amount must be > 0");
        require(destChain == 0 || destChain == 2, "Invalid chain (0=Solana, 2=Sui)");
        require(
            destAddress.length == 32 || destAddress.length == 20,
            "Invalid address length"
        );

        // Generate unique nonce for this burn
        uint256 nonce = uint256(keccak256(abi.encodePacked(
            msg.sender,
            amount,
            block.timestamp,
            block.number
        )));

        // Burn tokens from user
        wrappedSOL.burnFrom(msg.sender, amount);

        emit TokensBurned(
            msg.sender,
            amount,
            nonce,
            destChain,
            destAddress,
            block.timestamp
        );
    }

    /**
     * @notice Update the fee percentage
     * @param _feeBasisPoints New fee in basis points (max 10000)
     * @dev Only owner can update fees
     */
    function updateFee(uint256 _feeBasisPoints) external onlyOwner {
        require(_feeBasisPoints <= 10000, "Fee too high (max 10000 = 100%)");
        uint256 oldFee = feeBasisPoints;
        feeBasisPoints = _feeBasisPoints;
        emit FeeUpdated(oldFee, _feeBasisPoints);
    }

    /**
     * @notice Update the validator registry contract
     * @param _validatorRegistry New validator registry address
     * @dev Only owner can update the registry
     */
    function updateValidatorRegistry(address _validatorRegistry) external onlyOwner {
        require(_validatorRegistry != address(0), "Invalid registry address");
        address oldRegistry = address(validatorRegistry);
        validatorRegistry = ValidatorRegistry(_validatorRegistry);
        emit ValidatorRegistryUpdated(oldRegistry, _validatorRegistry);
    }

    /**
     * @notice Pause the contract (emergency stop)
     * @dev Only owner can pause
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause the contract
     * @dev Only owner can unpause
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Check if a nonce has been processed
     * @param nonce The nonce to check
     * @return bool True if the nonce has been used
     */
    function isNonceProcessed(uint256 nonce) external view returns (bool) {
        return processedNonces[nonce];
    }

    /**
     * @notice Get the current fee in basis points
     * @return uint256 Fee in basis points
     */
    function getFee() external view returns (uint256) {
        return feeBasisPoints;
    }
}

contracts/ValidatorRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";

/**
 * @title ValidatorRegistry
 * @notice Manages validator addresses and verifies multi-signature authentication
 * @dev Uses ECDSA for signature verification with configurable threshold
 */
contract ValidatorRegistry is Ownable {
    using ECDSA for bytes32;
    using MessageHashUtils for bytes32;

    mapping(address => bool) public isValidator;
    address[] public validators;
    uint256 public threshold;

    event ValidatorAdded(address indexed validator);
    event ValidatorRemoved(address indexed validator);
    event ThresholdUpdated(uint256 oldThreshold, uint256 newThreshold);

    /**
     * @notice Constructor initializes validators and threshold
     * @param _validators Array of initial validator addresses
     * @param _threshold Minimum number of signatures required
     */
    constructor(address[] memory _validators, uint256 _threshold) Ownable(msg.sender) {
        require(_validators.length >= _threshold, "Invalid threshold");
        require(_threshold > 0, "Threshold must be > 0");

        for (uint256 i = 0; i < _validators.length; i++) {
            require(_validators[i] != address(0), "Invalid validator");
            require(!isValidator[_validators[i]], "Duplicate validator");

            isValidator[_validators[i]] = true;
            validators.push(_validators[i]);
        }

        threshold = _threshold;
    }

    /**
     * @notice Add a new validator
     * @param validator Address of the validator to add
     * @dev Only owner can add validators
     */
    function addValidator(address validator) external onlyOwner {
        require(!isValidator[validator], "Already validator");
        require(validator != address(0), "Invalid address");

        isValidator[validator] = true;
        validators.push(validator);

        emit ValidatorAdded(validator);
    }

    /**
     * @notice Remove a validator
     * @param validator Address of the validator to remove
     * @dev Only owner can remove validators. Cannot remove if it breaks threshold
     */
    function removeValidator(address validator) external onlyOwner {
        require(isValidator[validator], "Not a validator");
        require(validators.length - 1 >= threshold, "Would break threshold");

        isValidator[validator] = false;

        // Remove from array
        for (uint256 i = 0; i < validators.length; i++) {
            if (validators[i] == validator) {
                validators[i] = validators[validators.length - 1];
                validators.pop();
                break;
            }
        }

        emit ValidatorRemoved(validator);
    }

    /**
     * @notice Update the signature threshold
     * @param _threshold New threshold value
     * @dev Only owner can update threshold
     */
    function updateThreshold(uint256 _threshold) external onlyOwner {
        require(_threshold > 0, "Threshold must be > 0");
        require(_threshold <= validators.length, "Threshold too high");

        uint256 oldThreshold = threshold;
        threshold = _threshold;
        emit ThresholdUpdated(oldThreshold, _threshold);
    }

    /**
     * @notice Verify that signatures meet the threshold requirement
     * @param messageHash Hash of the message that was signed
     * @param signatures Array of signatures to verify
     * @return bool True if signatures are valid and meet threshold
     */
    function verifySignatures(
        bytes32 messageHash,
        bytes[] calldata signatures
    ) external view returns (bool) {
        require(signatures.length >= threshold, "Not enough signatures");

        bytes32 ethSignedHash = messageHash.toEthSignedMessageHash();
        address[] memory signers = new address[](signatures.length);
        uint256 validCount = 0;

        for (uint256 i = 0; i < signatures.length; i++) {
            address signer = ethSignedHash.recover(signatures[i]);

            // Check if valid validator and not duplicate
            if (isValidator[signer] && !_contains(signers, signer, validCount)) {
                signers[validCount] = signer;
                validCount++;
            }
        }

        return validCount >= threshold;
    }

    /**
     * @notice Get the list of all validator addresses
     * @return address[] Array of validator addresses
     */
    function getValidators() external view returns (address[] memory) {
        return validators;
    }

    /**
     * @notice Get the count of validators
     * @return uint256 Number of validators
     */
    function getValidatorCount() external view returns (uint256) {
        return validators.length;
    }

    /**
     * @notice Check if an address array contains a specific address
     * @param arr Array to search
     * @param addr Address to find
     * @param len Length to search (optimization)
     * @return bool True if address is found
     */
    function _contains(address[] memory arr, address addr, uint256 len)
        private pure returns (bool)
    {
        for (uint256 i = 0; i < len; i++) {
            if (arr[i] == addr) return true;
        }
        return false;
    }
}

contracts/WrappedSOL.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title WrappedSOL
 * @notice ERC-20 token representing SOL on Ethereum
 * @dev Only the bridge contract can mint and burn tokens
 */
contract WrappedSOL is ERC20, Ownable {
    address public bridge;

    event BridgeUpdated(address indexed oldBridge, address indexed newBridge);

    /**
     * @notice Constructor initializes the token with name and symbol
     */
    constructor() ERC20("Wrapped Solana", "wSOL") Ownable(msg.sender) {}

    /**
     * @notice Set the bridge contract address
     * @param _bridge Address of the bridge contract
     * @dev Only owner can set the bridge address
     */
    function setBridge(address _bridge) external onlyOwner {
        require(_bridge != address(0), "Invalid bridge address");
        address oldBridge = bridge;
        bridge = _bridge;
        emit BridgeUpdated(oldBridge, _bridge);
    }

    /**
     * @notice Mint tokens to an address
     * @param to Recipient address
     * @param amount Amount to mint
     * @dev Only bridge can mint
     */
    function mint(address to, uint256 amount) external {
        require(msg.sender == bridge, "Only bridge can mint");
        require(to != address(0), "Cannot mint to zero address");
        _mint(to, amount);
    }

    /**
     * @notice Burn tokens from an address
     * @param from Address to burn from
     * @param amount Amount to burn
     * @dev Only bridge can burn
     */
    function burnFrom(address from, uint256 amount) external {
        require(msg.sender == bridge, "Only bridge can burn");
        _burn(from, amount);
    }

    /**
     * @notice Get token decimals (18 to match SOL precision)
     */
    function decimals() public pure override returns (uint8) {
        return 18;
    }
}

